  const handleSubmission = async (e) => {
    e.preventDefault();
    if (validateForm()) {
    const formData = new FormData();
    if(name == ''){
      alert("enter name")
    }
    else{
      formData.append("name", name);
      formData.append("email", email);
      formData.append("phonenumber", phonenumber);
      formData.append("file", selectedFile);
    }


    axios.post(API_PATH,formData,{
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    }) // 4
       .then(response => {

      console.log("response",response);
      alert("Form submitted");
       }
      ) // 5
       .catch(errors => { this.submitSpinner = false; console.log(errors) })
      }
  };


  const deleteImage = (productId) =>
  {
    axios.get('http://localhost:8000/api/delete/'+productId)
    .then((result)=>{
      loadList();
    })
    .catch(()=>{
      alert('Error in the Code');
    });
  };

  return (
    <div className="container mt-5">

      <div className="row">
        <div className="col-sm-6 m-auto bg-gray">
         <div className="card">
           <div className="card-header bg-info"><h5 className="text-center text-light">Career</h5></div>
          <div className="card-body text-start">



              <div className="form-group">
                <label>Full Name</label>

                <input {...register('name', { required: true })} type="text" name="name" className="form-control rounded-0" onChange={(e) => setName(e.target.value)} placeholder="FUll Name"/>
