import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useForm } from 'react-hook-form';
import * as yup from 'yup';
const API_PATH = 'http://localhost/Mr_amiit/cubedotsProjects/reactForm/submitform/index.php';



// const SignupSchema = yup.object().shape({
//   name: yup.string().required(),
// });

function UserForm() {
  const [content, setContent] = React.useState("");
  const [file, setSelectedFile] = ReactuseState(null);
  const [loadimage, setLoadImage] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phonenumber, setPhonenumber] = useState("");

  const { register, formState: { errors } } = useForm();


  useEffect(() => {
    loadList();
  }, []);

  const loadList = async () => {
    // const result = await axios.get("http://localhost:8000/api/list");
    // setLoadImage(result.data.reverse());
  };


  const handleSubmit  = async (e) => {
    e.preventDefault();

    setContent(""); //Resets the value of the first input - See #1

    //////START of File Input Reset
    imageInputRef.current.value = "";//Resets the file name of the file input - See #2
    setImage(null); 
    const formData = new FormData();

    formData.append("name", name);
    formData.append("email", email);
    formData.append("phonenumber", phonenumber);
    formData.append("file", file);

    await axios.post(API_PATH,formData,{
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    }) // 4
       .then(response => {
      if(response.data.sent === true){
        setName('')
        setEmail('')
        setPhonenumber('')
        setSelectedFile('')

        alert(response.data.message)
      }
      else{
        // setName('')
        // setEmail('')
        // setPhonenumber('')
        setSelectedFile('')
        alert(response.data.message)
      }

       }
      ) // 5
       .catch(errors => { this.submitSpinner = false; console.log(errors) })
  };


  const deleteImage = (productId) =>
  {
    axios.get('http://localhost:8000/api/delete/'+productId)
    .then((result)=>{
      loadList();
    })
    .catch(()=>{
      alert('Error in the Code');
    });
  };

  return (
    <div className="container mt-5">

      <div className="row">
        <div className="col-sm-6 m-auto bg-gray">
         <div className="card">
           <div className="card-header bg-info"><h5 className="text-center text-light">Career</h5></div>
          <div className="card-body text-start">

          <form onSubmit={handleSubmit}>

              <div className="form-group">
                <label>Full Name</label>

                <input type="text" name="name" className="form-control rounded-0" onChange={(e) => setName(e.target.value)} placeholder="Full Name" value={name || ''} required/>
                {errors.name && <p>{errors.name.message}</p>}
              </div>



             <div className="form-group mt-2">
             <label>Email</label>

              <input type="text" name="email"  className="form-control rounded-0" onChange={(e) => setEmail(e.target.value)} placeholder="Email" value={email || ''} required />

             </div>

             <div className="form-group mt-2">
             <label>Contact no.</label>

              <input type="text" name="phonenumber" className="form-control rounded-0" onChange={(e) => setPhonenumber(e.target.value)} placeholder="Contact no." value={phonenumber || ''} required />
             </div>

             <div className="form-group mt-2">
             <label>Upload CV</label>
              <input type="file" name="file" className="form-control rounded-0" onChange={(e) => setSelectedFile(e.target.files[0])} key={file || null}  required />
             </div>



             <div className="form-group mt-2">
               <button type="submit" className="btn btn-success" name="submit">
                 Submit
              </button>
             </div>

          </form>
          </div>
          </div>
        </div>
        <div className="col-sm-9">
          <div className="row">
            {loadimage.map((name) => (
              <div className="col-sm-3">
                <div className="card mb-3" style={{width:"12rem"}}>
                    <h5><a  href="#" onClick={() => deleteImage(name.id)} style={{textDecoration:"none",marginLeft:"162px"}}>
                      <span aria-hidden="true" className="text-danger">&times;</span>
                    </a></h5>
                    <img className="card-img-top hover-shadow" src={"http://localhost:8000/" + name.img_path} alt="Card image cap" style={{height:"110px"}}/>

                    <div className="card-body">
                    <h6>{name.name}</h6>
                      <span className="card-text">{name.description}</span>
                    </div>
                  </div>
                </div>
              ))}
           </div>
        </div>
      </div>
    </div>
  );
}
export default UserForm;
