import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
const API_PATH = 'http://localhost/Mr_amiit/cubedotsProjects/reactForm/submitform/index.php';

function UserForm() {
  const [selectedFile, setSelectedFile] = useState();
  const [loadimage, setLoadImage] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phonenumber, setPhonenumber] = useState("");


  useEffect(() => {
    loadList();
  }, []);

  const loadList = async () => {
    // const result = await axios.get("http://localhost:8000/api/list");
    // setLoadImage(result.data.reverse());
  };
  function validateForm() {
    console.log("check validation")


    if (name == "") {
      formIsValid = false;
      errors["fullName"] = "*Please enter your full name.";
    }

    if (typeof fields["fullName"] !== "undefined") {
      if (!fields["fullName"].match(/^[a-zA-Z ]*$/)) {
        formIsValid = false;
        errors["fullName"] = "*Please enter alphabet characters only.";
      }
    }

    if (!fields["emailid"]) {
      formIsValid = false;
      errors["emailid"] = "*Please enter your email-ID.";
    }

    if (typeof fields["emailid"] !== "undefined") {
      //regular expression for email validation
      var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
      if (!pattern.test(fields["emailid"])) {
        formIsValid = false;
        errors["emailid"] = "*Please enter valid email-ID.";
      }
    }

    if (phonenumber == "") {
      formIsValid = false;
      errors["mobileno"] = "*Please enter your mobile no.";
    }

    // if (typeof fields["mobileno"] !== "undefined") {
    //   if (!fields["mobileno"].match(/^[0-9]{10}$/)) {
    //     formIsValid = false;
    //     errors["mobileno"] = "*Please enter valid mobile no.";
    //   }
    // }

    // if (!fields["uploadcv"]) {
    //   formIsValid = false;
    //   errors["uploadcv"] = "*Please upload your CV.";
    // }

    // if (typeof fields["uploadcv"] !== "undefined") {
    //   if (!fields["uploadcv"]) {
    //     formIsValid = false;
    //     errors["uploadcv"] = "*Please Upload your file in valid format.";
    //   }
    // }


    return formIsValid;


  };

  const handleSubmission = async (e) => {
    e.preventDefault();
    if (validateForm()) {
    const formData = new FormData();
    if(name == ''){
      alert("enter name")
    }
    else{
      formData.append("name", name);
      formData.append("email", email);
      formData.append("phonenumber", phonenumber);
      formData.append("file", selectedFile);
    }


    axios.post(API_PATH,formData,{
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    }) // 4
       .then(response => {

      console.log("response",response);
      alert("Form submitted");
       }
      ) // 5
       .catch(errors => { this.submitSpinner = false; console.log(errors) })
      }
  };


  const deleteImage = (productId) =>
  {
    axios.get('http://localhost:8000/api/delete/'+productId)
    .then((result)=>{
      loadList();
    })
    .catch(()=>{
      alert('Error in the Code');
    });
  };

  return (
    <div className="container mt-5">

      <div className="row">
        <div className="col-sm-6 m-auto bg-gray">
         <div className="card">
           <div className="card-header bg-info"><h5 className="text-center text-light">Career</h5></div>
          <div className="card-body text-start">



              <div className="form-group">
                <label>Full Name</label>

                <input type="text" name="name" className="form-control rounded-0" onChange={(e) => setName(e.target.value)} placeholder="FUll Name" required/>
              </div>



             <div className="form-group mt-2">
             <label>Email</label>

              <input type="text" name="email" className="form-control rounded-0" onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
             </div>

             <div className="form-group mt-2">
             <label>Contact no.</label>

              <input type="text" name="phonenumber" className="form-control rounded-0" onChange={(e) => setPhonenumber(e.target.value)} placeholder="Contact no." required />
             </div>

             <div className="form-group mt-2">
             <label>Upload CV</label>
              <input type="file" name="file" className="form-control rounded-0" onChange={(e) => setSelectedFile(e.target.files[0])} required/>
             </div>



             <div className="form-group mt-2">
               <button type="submit" onClick={handleSubmission} className="btn btn-success" name="submit">
                 Submit
              </button>
             </div>


          </div>
          </div>
        </div>
        <div className="col-sm-9">
          <div className="row">
            {loadimage.map((name) => (
              <div className="col-sm-3">
                <div className="card mb-3" style={{width:"12rem"}}>
                    <h5><a  href="#" onClick={() => deleteImage(name.id)} style={{textDecoration:"none",marginLeft:"162px"}}>
                      <span aria-hidden="true" className="text-danger">&times;</span>
                    </a></h5>
                    <img className="card-img-top hover-shadow" src={"http://localhost:8000/" + name.img_path} alt="Card image cap" style={{height:"110px"}}/>

                    <div className="card-body">
                    <h6>{name.name}</h6>
                      <span className="card-text">{name.description}</span>
                    </div>
                  </div>
                </div>
              ))}
           </div>
        </div>
      </div>
    </div>
  );
}
export default UserForm;
