import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState, useEffect } from "react";
import axios from "axios";

function UserForm() {
  const [selectedFile, setSelectedFile] = useState();
  const [loadimage, setLoadImage] = useState([]);
  const [name, setName] = useState("");
  const [desc, setDesc] = useState("");

  useEffect(() => {
    loadList();
  }, []);

  const loadList = async () => {
    // const result = await axios.get("http://localhost:8000/api/list");
    // setLoadImage(result.data.reverse());
  };


  const handleSubmission = async (e) => {
    const formData = new FormData();

    formData.append("name", name);
    formData.append("desc", desc);
    formData.append("file", selectedFile);
    await fetch("http://localhost/Mr_amiit/cubedotsProjects/reactForm/submitform/index.php", {
      method: "POST",
      body: formData,
    })
    .then((result)=>{
      loadList();
    })
    .catch(()=>{
      alert('Error in the Code');
    });
  };

  const deleteImage = (productId) =>
  {
    axios.get('http://localhost:8000/api/delete/'+productId)
    .then((result)=>{
      loadList();
    })
    .catch(()=>{
      alert('Error in the Code');
    });
  };

  return (
    <div className="container mt-5">

      <div className="row">
        <div className="col-sm-6 m-auto bg-gray">
         <div className="card">
           <div className="card-header bg-info"><h5 className="text-center text-light">Career</h5></div>
          <div className="card-body text-start">



              <div className="form-group">
                <label>Full Name</label>

                <input type="text" name="name" className="form-control rounded-0" onChange={(e) => setName(e.target.value)} placeholder="FUll Name" required/>
              </div>



             <div className="form-group mt-2">
             <label>Email</label>

              <input type="text" name="email" className="form-control rounded-0" onChange={(e) => setDesc(e.target.value)} placeholder="Email" required />
             </div>

             <div className="form-group mt-2">
             <label>Contact no.</label>

              <input type="text" name="phonenumber" className="form-control rounded-0" onChange={(e) => setDesc(e.target.value)} placeholder="Contact no." required />
             </div>

             <div className="form-group mt-2">
             <label>Upload CV</label>
              <input type="file" name="file" className="form-control rounded-0" onChange={(e) => setSelectedFile(e.target.files[0])} required/>
             </div>



             <div className="form-group mt-2">
               <button type="submit" onClick={handleSubmission} className="btn btn-success" name="submit">
                 Submit
              </button>
             </div>


          </div>
          </div>
        </div>
        <div className="col-sm-9">
          <div className="row">
            {loadimage.map((name) => (
              <div className="col-sm-3">
                <div class="card mb-3" style={{width:"12rem"}}>
                    <h5><a  href="#" onClick={() => deleteImage(name.id)} style={{textDecoration:"none",marginLeft:"162px"}}>
                      <span aria-hidden="true" className="text-danger">&times;</span>
                    </a></h5>
                    <img class="card-img-top hover-shadow" src={"http://localhost:8000/" + name.img_path} alt="Card image cap" style={{height:"110px"}}/>

                    <div class="card-body">
                    <h6>{name.name}</h6>
                      <span class="card-text">{name.description}</span>
                    </div>
                  </div>
                </div>
              ))}
           </div>
        </div>
      </div>
    </div>
  );
}
export default UserForm;
