import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState, useEffect } from "react";
import axios from "axios";

function UserForm() {
  const [selectedFile, setSelectedFile] = useState();
  const [loadimage, setLoadImage] = useState([]);
  const [name, setName] = useState("");
  const [desc, setDesc] = useState("");

  useEffect(() => {
    loadList();
  }, []);

  const loadList = async () => {
    const result = await axios.get("http://localhost:8000/api/list");
    setLoadImage(result.data.reverse());
  };


  const handleSubmission = async (e) => {
    const formData = new FormData();
    formData.append("file", selectedFile);
    formData.append("name", name);
    formData.append("desc", desc);
    await fetch("http://localhost:8000/api/upload", {
      method: "POST",
      body: formData,
    })
    .then((result)=>{
      loadList();
    })
    .catch(()=>{
      alert('Error in the Code');
    });
  };

  const deleteImage = (productId) =>
  {
    axios.get('http://localhost:8000/api/delete/'+productId)
    .then((result)=>{
      loadList();
    })
    .catch(()=>{
      alert('Error in the Code');
    });
  };

  return (
    <div className="container">

      <div className="row">
        <div className="col-sm-6 m-auto bg-gray">
         <div className="card">
           <div className="card-header bg-info"><h5 class="text-center text-light">Career</h5></div>
          <div>



              <div class="form-group">
                <input type="text" name="name" className="form-control" onChange={(e) => setName(e.target.value)} placeholder="Country Name"/>
              </div>



             <div class="form-group">
              <textarea type="text" name="desc" className="form-control"  rows="3" cols="23" onChange={(e) => setDesc(e.target.value)} placeholder="Write Description" />
             </div>



             <div class="form-group">
              <input type="file" name="file" className="form-control" onChange={(e) => setSelectedFile(e.target.files[0])} />
             </div>



             <div class="form-group">
               <button type="submit" onClick={handleSubmission}class="btn btn-success mb-3" name="submit">
                 Add Gallary
              </button>
             </div>


          </div>
          </div>
        </div>
        <div className="col-sm-9">
          <div className="row">
            {loadimage.map((name) => (
              <div className="col-sm-3">
                <div class="card mb-3" style={{width:"12rem"}}>
                    <h5><a  href="#" onClick={() => deleteImage(name.id)} style={{textDecoration:"none",marginLeft:"162px"}}>
                      <span aria-hidden="true" className="text-danger">&times;</span>
                    </a></h5>
                    <img class="card-img-top hover-shadow" src={"http://localhost:8000/" + name.img_path} alt="Card image cap" style={{height:"110px"}}/>

                    <div class="card-body">
                    <h6>{name.name}</h6>
                      <span class="card-text">{name.description}</span>
                    </div>
                  </div>
                </div>
              ))}
           </div>
        </div>
      </div>
    </div>
  );
}
export default UserForm;
